publications_by_year.map:
function(doc) 
{ 
  var types = ['article','book','chapter','generic','journal'];
  var type = types.indexOf(doc.type);
  if (type != -1) 
  {

  if (doc.year)
  {
     emit(doc.year, 1);
  }
}
}

names_by_year.map:
function(doc) 
{ 
  if (doc.type == 'nameCluster')
  {
    var weight = doc.year.length;
    for (i in doc.year)
    {
       emit(doc.year[i], 1/weight);
   }
  }
}

identifier.map:
function(doc) 
{ if (doc.identifier) 
 {    
  for (var i in doc.identifier)    
  {      
    emit(doc.identifier[i].type, 1);     
   }     
  }
}

link.map:
function(doc) 
{ if (doc.link) 
 {    
  for (var i in doc.link)    
  {      
    emit(doc.link[i].anchor, 1);     
   }     
  }
}
